function reveal() {
        const replay = document.querySelector('.ddpo-replay');
        replay.style.display = 'flex';
    }

    window.onload = () => {
        const replay = document.querySelector('.ddpo-replay');

        replay.addEventListener('click', () => {
            const video = document.querySelector('.ddpo-video');
            video.currentTime = 0;
            video.play();
            replay.style.display = 'none';
        });
    }


